const mongo = require('mongodb')
let client = null
let db = null
export async function init() {
    if(!client) {
        client = await mongo.MongoClient.connect("mongodb://localhost")
        db = client.db("youtube-sapper")
    }
    console.log(`ðŸš€ ~ file: mongo.js ~ line 9 ~ init ~ client`, client)
    console.log(`ðŸš€ ~ file: mongo.js ~ line 10 ~ init ~ db`, db)

    return { client, db }
}